import { Box, Card, List, Typography } from "@mui/material";
import TodoItem from "./TodoItem";
import { useTodos } from "../graphql/hooks/todoHooks";
import { useUserContext } from "../Providers/UserProvider";
import styled from "@emotion/styled";

export default function TodoList() {
  const { user } = useUserContext();
  const { todos } = useTodos({ userEmail: user?.email });

  const shouldDisplayEmptyState = !todos?.length;

  return (
    <>
      <ListContainer shouldDisplayEmptyState={shouldDisplayEmptyState}>
        {todos?.length ? (
          <List>
            {todos?.map((todo, idx) => (
              <TodoItem key={todo.id} todo={todo} idx={idx} />
            ))}
          </List>
        ) : (
          <EmptyStateContainer>
            <EmptyState>Create a task to get started</EmptyState>
          </EmptyStateContainer>
        )}
      </ListContainer>
    </>
  );
}

const ListContainer = styled(Card)(({ shouldDisplayEmptyState }) => ({
  display: "flex",
  height: "50vh",
  backgroundColor: "rgb(33,41,54)",
  width: "60vw",
  margin: "2rem auto",
  justifyContent: shouldDisplayEmptyState ? "center" : "none",
}));

const EmptyState = styled(Typography)(() => ({
  color: "gray",
  fontSize: "1.5rem",
}));

const EmptyStateContainer = styled(Box)(() => ({
  display: "flex",
  justifyContent: "center",
}));
