import { gql } from "@apollo/client";

const GET_TODOS = gql`
  {
    getTodos {
      id
      title
      details
      date
    }
  }
`;

const GET_TODO = gql`
  query getTodo($id: ID) {
    getTodo(id: $id) {
      id
      title
      details
      date

`

export { GET_TODOS };
