import { gql } from "@apollo/client";

export const CREATE_TODO = gql`
  mutation createTodo($title: String, $details: String, $date: Date) {
    createTodo(title: $title, details: $details, date: $date) {
      id
      title
      details
      date
    }
  }
`;
