import { Input, Button } from "@mui/material";
import FormControl from "@mui/material/FormControl";
import { useMutation } from "@apollo/client";
import { CREATE_TODO } from "../graphql/mutations/Todo";
import { GET_TODOS } from "../graphql/queries/Todo";

export default function TodoForm() {
  const [createTodo] = useMutation(CREATE_TODO);
  return (
    <form onSubmit={(e) => handleSubmit({ e, createTodo })}>
      <FormControl>
        <Input name="title" id="title-input" placeholder="Title" />
        <Input name="details" id="details-input" placeholder="Details" />
        <Input name="date" type="date" id="date-input" placeholder="Date" />
        <Button variant="contained" color="primary" type="submit">
          Submit
        </Button>
      </FormControl>
    </form>
  );
}

async function handleSubmit({ e, createTodo }) {
  e.preventDefault();
  const title = e.target.title.value;
  const details = e.target.details.value;
  const date = e.target.date.value;

  const { data: newTodoData } = await createTodo({
    variables: {
      title,
      details,
      date,
    },
    refetchQueries: [{ query: GET_TODOS }],
  });
}
