import { Input, Button, Card } from "@mui/material";
import FormControl from "@mui/material/FormControl";
import { useCreateTodo } from "../graphql/hooks/todoHooks";
import { useUserContext } from "../Providers/UserProvider";
import styled from "@emotion/styled";
import { useState } from "react";

export default function TodoForm() {
  const { createTodo } = useCreateTodo();
  const { user } = useUserContext();
  const [titleInputValue, setTitleInputValue] = useState("");

  return (
    <form
      onSubmit={(e) => handleSubmit({ e, createTodo, userEmail: user.email })}
    >
      <FormControl>
        <InputContainer>
          <TitleInput
            name="title"
            id="title-input"
            placeholder="Title"
            required
            disableUnderline
            value={titleInputValue}
          />
          <Button type="submit">Add</Button>
        </InputContainer>
      </FormControl>
    </form>
  );
}

const TitleInput = styled(Input)({
  color: "white",
  margin: "0.5rem",
  border: "none",
});

const InputContainer = styled(Card)({
  backgroundColor: "rgb(33,41,54)",
  height: "5vh",
  width: "60vw",
  display: "flex",
  justifyContent: "space-between",
});

async function handleSubmit({ e, createTodo, userEmail }) {
  e.preventDefault();
  const title = e.target.title.value;

  await createTodo({
    title,
    userEmail,
  });
}
