import { gql } from "@apollo/client";

export const CREATE_TODO = gql`
  mutation createTodo(
    $title: String
    $details: String
    $date: Date
    $user_email: String
  ) {
    createTodo(
      title: $title
      details: $details
      date: $date
      user_email: $user_email
    ) {
      id
      title
      details
      date
      user_email
    }
  }
`;

export const DELETE_TODO = gql`
  mutation deleteTodo($id: ID) {
    deleteTodo(id: $id) {
      user_email
    }
  }
`;

export const UPDATE_TODO = gql`
  mutation updateTodo($id: ID, $title: String, $date: Date, $details: String) {
    updateTodo(id: $id, title: $title, date: $date, details: $details)
  }
`;
