import { useMutation, useQuery } from "@apollo/client";
import { GET_TODOS } from "../queries/Todo";
import { CREATE_TODO } from "../mutations/Todo";

export const useTodos = () => {
  const { error, loading: isLoading, data } = useQuery(GET_TODOS);

  const todos = data?.getTodos;

  return { error, todos, isLoading };
};

export const useCreateTodo = () => {
  const [createTodoMutation, { error, loading: isLoading }] =
    useMutation(CREATE_TODO);

  const createTodo = ({ title, details, date }) => {
    createTodoMutation({
      variables: {
        title: title,
        details: newTodo.details,
        date: newTodo.date,
      },
      update(cache, { data }) {
        updateTodoCache({ cache, newTodo: data.createTodo });
      },
    });
  };

  return { createTodo, error, isLoading };
};

function updateTodoCache({ cache, newTodo }) {
  const { getTodos } = cache.readQuery({ query: GET_TODOS });
  cache.writeQuery({
    query: GET_TODOS,
    data: { getTodos: [newTodo, ...getTodos] },
  });
}
