export default function TodoItemIcons({
  itemId,
  titleInputValue,
  updateTodo,
  setIsUpdating,
}) {
  return (
    <IconContainer>
      {isUpdating ? (
        <StyledDoneIcon
          onClick={() =>
            handleUpdate({
              id: todo.id,
              titleInputValue,
              updateTodo,
              setIsUpdating,
            })
          }
        />
      ) : (
        <StyledUpdateIcon onClick={() => setIsUpdating(true)} />
      )}
      <StyledDeleteIcon
        onClick={() => handleDelete({ id: todo.id, deleteTodo })}
      />
    </IconContainer>
  );
}
