import DeleteIcon from "@mui/icons-material/Delete";
import EditIcon from "@mui/icons-material/Edit";
import DoneIcon from "@mui/icons-material/Done";
import { Box, Input, Typography } from "@mui/material";
import styled from "@emotion/styled";
import { useState } from "react";
import TodoItemIcons from "./TodoItemIcons";

export default function TodoItem({ todo, idx, shouldDisplayIcons = true }) {
  const [isUpdating, setIsUpdating] = useState(false);
  const [titleInputValue, setTitleInputValue] = useState(todo.title);
  const shouldDisplayTopBorder = idx === 0;

  return (
    <ItemContainer
      key={todo.id}
      shouldDisplayTopBorder={shouldDisplayTopBorder}
    >
      {isUpdating ? (
        <StyledTitleInput
          name="title"
          id="update-title-input"
          required
          disableUnderline
          value={titleInputValue}
          onChange={(e) => setTitleInputValue(e.target.value)}
        />
      ) : (
        <ItemTitle>{titleInputValue}</ItemTitle>
      )}
      {shouldDisplayIcons && (
        <TodoItemIcons
          itemId={todo.id}
          titleInputValue={titleInputValue}
          setIsUpdating={setIsUpdating}
          isUpdating={isUpdating}
        />
      )}
    </ItemContainer>
  );
}

const ItemContainer = styled(Box)(({ shouldDisplayTopBorder }) => ({
  display: "flex",
  flexDirection: "row",
  alignItems: "center",
  justifyContent: "space-between",
  gap: "1rem",
  borderTop: shouldDisplayTopBorder ? "1px solid white" : "none",
  borderBottom: "1px solid white",
  marginLeft: "0.5rem",
  marginRight: "0.5rem",
}));



const ItemTitle = styled(Typography)(() => ({
  marginTop: "1rem",
  marginBottom: "1rem",
  color: "white",
}));

const StyledTitleInput = styled(Input)({
  color: "white",
  marginTop: "1rem",
  marginBottom: "1rem",
  width: "20rem",
});

const StyledDoneIcon = styled(DoneIcon)({
  color: "lightgreen",
  cursor: "pointer",
});

async function handleDelete({ id, deleteTodo }) {
  await deleteTodo({ id });
}

async function handleUpdate({
  id,
  titleInputValue,
  updateTodo,
  setIsUpdating,
}) {
  await updateTodo({
    id,
    title: titleInputValue,
  });

  setIsUpdating(false);
}
