import { Input, Button } from "@mui/material";
import FormControl from "@mui/material/FormControl";
import { useCreateTodo } from "../graphql/hooks/todoHooks";
import { useUserContext } from "../Providers/UserProvider";

export default function TodoForm() {
  const { createTodo } = useCreateTodo();
  const { user } = useUserContext();

  return (
    <form
      onSubmit={(e) => handleSubmit({ e, createTodo, userEmail: user?.email })}
    >
      <FormControl>
        <Input name="title" id="title-input" placeholder="Title" required />
        <Input
          name="details"
          id="details-input"
          placeholder="Details"
          required
        />
        <Input
          name="date"
          type="date"
          id="date-input"
          placeholder="Date"
          required
        />
        <Button variant="contained" color="primary" type="submit">
          Submit
        </Button>
      </FormControl>
    </form>
  );
}

async function handleSubmit({ e, createTodo }) {
  e.preventDefault();
  const title = e.target.title.value;
  const details = e.target.details.value;
  const date = e.target.date.value;

  await createTodo({
    title,
    details,
    date,
  });
}
