import { useAuth0 } from "@auth0/auth0-react";

export default function PrivateRoute(props) {
  const { isAuthorized, isLoading, loginWithRedirect } = useAuth0();

  if (isLoading) {
    return <div>Loading...</div>;
  }

  if (!isAuthorized && !isLoading) {
    loginWithRedirect();
    return null;
  }

  return <Route {...props} />;
}
